        ))
    }

    return (
        <div>
            <h2> Selecciona el menú </h2>
            <button className="btn btn-primary btn-block mx-2" onClick={()=> openMenu()}> Mañana </button>
            <button className="btn btn-primary btn-block" onClick={()=> openMenu()}> Tarde </button>

        </div>
    )
}

