import React from 'react'
import menu from '../menu/menu.json'
//import { nanoid } from 'nanoid'


const Menu = () => {
    const data = menu.products;
    const [products, setProducts] = React.useState([]);
    const [food, setFood] = React.useState([])

    React.useEffect(()=> {
       setProducts(data)
        setFood(data)
    },[])

    const openMenu = (select) => {
        if (select === 'desayuno'){
        const desayuno = data.filter((p)=> p.time === 'mañana');
        const comida = desayuno.filter((p)=> p.category === 'plato');
        setProducts(desayuno)
        setFood(comida)

    } else if(select === 'tarde') {
        const tarde = data.filter((p)=> p.time === 'tarde');
        const comida = tarde.filter((p)=> p.category === 'plato');
        setProducts(tarde)
        setFood(food)
    } else {
        console.log('error')
    }
    }



    return (
        <div>
            <h2> Selecciona el menú </h2>
            <button className="btn btn-primary btn-block mx-2" onClick={()=> openMenu('desayuno')}> Mañana </button>
            <button className="btn btn-primary btn-block" onClick={()=> openMenu('tarde')}> Tarde </button>

            <h2> Productos en venta </h2>
            {
                products.map((item, id) => (
                    <button type= "button" className="btn btn-outline-info bt-lg" key={id}>{item.name} - ${item.price} </button>
                ))

            }

            <h2> Comida </h2>
                    {
                    food.map((item, index) => (
                        <p class="mt-3 col-md-">
                    <button type= "button" className="btn btn-outline-info bt-lg" key={index}>{item.name} - ${item.price} </button>
                    </p>
                ))

                    }
                <h2> Bebestibles </h2>
                    {
                    food.map((item, index) => (
                        <p class="mt-3 col-md-">
                    <button type= "button" className="btn btn-outline-info bt-lg" key={index}>{item.name} - ${item.price} </button>
                    </p>
                ))

                    }
        </div>
    )
}

export default Menu
