import React from 'react'
import menu from '../menu/menu.json'

const menuDetails = menu;
const Menu = () => {

    const openMenu = () => {
        menuDetails.map((item) => (
            <h4 key={item.id}>{item.texto} </h4>
        ))
    }

    return (
        <div>
            <h2> Selecciona el menú </h2>
            <button className="btn btn-primary btn-block mr-5" onClick={()=> openMenu()}> Mañana </button>
            <button className="btn btn-primary btn-block"> Tarde </button>

        </div>
    )
}

export default Menu
