import React from 'react'
import menu from '../menu/menu.json'
//import { nanoid } from 'nanoid'


const Menu = () => {
    const data = menu.products;
    const [products, setProducts] = React.useState(data);

    const openMenu = (select) => {
        if (select === 'desayuno'){
        const desayuno = products.filter((p)=> p.time === 'mañana');
        console.log(desayuno)
    } else if(select === 'tarde') {
        const desayuno = products.filter((p)=> p.time === 'mañana');
        console.log(desayuno)
    }
    }

    return (
        <div>
            <h2> Selecciona el menú </h2>
            <button className="btn btn-primary btn-block mx-2" onClick={()=> openMenu()}> Mañana </button>
            <button className="btn btn-primary btn-block"> Tarde </button>

        </div>
    )
}

export default Menu
